while sleep 1; do clear; mysql drcron -e 'create temporary table lastlogs select execution_id, max(id) as id from job_execution_logs group by execution_id; select jobs.id , scheduled_start, started, if(started is not null,now()-started,NULL) as runtime, jobs.schedule, jobs.run_parallel, jobs.command, replace(job_execution_logs.output,"\n","") from job_executions join jobs on job_id = jobs.id left join lastlogs on job_executions.id = lastlogs.execution_id left  join job_execution_logs on lastlogs.id = job_execution_logs.id  where ended is null;'; done
